import { Link,useHistory } from 'react-router-dom';
import React, { useState } from 'react';
import './Login.css';
import { auth } from "./firebase";
function Login() {
    const [email,setEmail]=useState('');
    const [password,setPassword]=useState('');
    const history=useHistory();

    // const signIn=e=>{
    //     e.preventDefault();
    //     //some fancy database with firebase
    //     auth.signInWithEmailAndPassword(email,password)
    //         .then(auth=>{
    //             history.push('/')
    //         })
    //         .catch(error=>alert(error.message))



    // };
    const register=e=>{
        e.preventDefault();
        //some fance firebase registration

        auth
            .createUserWithEmailAndPassword(email,password)
            .then((auth)=>{
            
                if(auth){
                    history.push('/');
                }
            })
            .catch(error=>alert(error.message));

    };
    return (
        <div className="login">
            <Link to="/login">
                <img className="login_logo"alt=""
                     src="loginlogo.png"/>
            
            </Link>
            <div className="login_container">
                <h1>Sign Up</h1>

                <form>
                    <h5>Email:</h5>
                    <input type='text' value={email}/>

                    <h5>Password:</h5>
                    <input type='password' value={password}/>

                    <button type="submit"  className="login_signinButton">Sign in</button> 
                    
                </form>
                <p>
                    By Siging in you agree to the amazon
                    Fake Clone conditions.
                </p>
                <button onClick={register}
                className="login_registerButton">Create Your Account</button>

            </div>
            
        </div>
    )
}

export default Login
