                <img className="checkout_ad" src="amazon_ad.png"/>
            
                <div className="checkout_title">
                    <h2>Your Basket Here</h2>
                    {/*Checkout Product*/}
                    <h2>  {basket.map(item=>(
                        <CheckoutProduct
